import axios from 'axios';
import React, {useEffect, useState} from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Moment from 'react-moment'

import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import ArrowDropUpIcon from '@material-ui/icons/ArrowDropUp';

const columns = [
    {id: 'personID', label: '환자 ID', align: 'center'},
    {id: 'gender', label: '성별', align: 'center'},
    {id: 'birthDatetime', label: '생년월일', align: 'center'},
    {id: 'age', label: '나이', align: 'center'},
    {id: 'race', label: '인종', align: 'center'},
    {id: 'ethnicity', label: '민족', align: 'center'},
    {id: 'isDeath', label: '사망여부', align: 'center'}
]


const Patient_list = () =>{
    const baseurl = 'http://49.50.167.136:9871/'

    const [plist,setPlist] = useState([]);
    const [page, setPage] = useState(1);
    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [order_column, setOrder_column] = useState();

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    }

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(1);
    }

    const arrowHandler = ((e)=>{
        if (order_column !== e) {
            setOrder_column(e)
        } else {
            setOrder_column()
        }
    })

    useEffect(()=>{
        axios.get(baseurl+`api/patient/list?page=${page}&order_desc=false`)
        .then((res)=>{
            console.log(res.data.patient)
            setPlist(res.data.patient.list)
        }).catch((err)=>{
            console.log(err)
        })
    },[])
    console.log(plist)


    return(
        <div>
            <p>Patient_list</p>
            <TableContainer >
                    <Table stickyHeader aria-label="sticky table">
                    <TableHead>
                        <TableRow>
                        {columns.map((column) => (
                            <TableCell
                            key={column.id}
                            align={column.align}
                            >
                            {column.label}
                            {order_column === column.id ?  <ArrowDropUpIcon onClick={()=>arrowHandler(column.id)}/> :<ArrowDropDownIcon onClick={()=>arrowHandler(column.id)}/>}
                            </TableCell>
                        ))}
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {plist.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((patient, index) => {
                        return (
                            <TableRow hover tabIndex={-1} key={index}>
                            {columns.map((column) => {
                                const value = patient[column.id];
                                return (

                                <TableCell key={column.id} align={column.align}>
                                  {/* {column.format && typeof value === 'number' ? column.format(value) : value} */}
                                    {column.id !== "birthDatetime" && column.id !== "isDeath" && value}
                                    {column.id === "birthDatetime" && <Moment format="YYYY-MM-DD HH:mm">{value}</Moment>}
                                    {column.id === "isDeath" && value === false && <span>N</span>}
                                    {column.id === "isDeath" && value === true && <span>Y</span>}
                                </TableCell>  
                                );
                            })}
                            </TableRow>
                        );
                        })}
                    </TableBody>
                    </Table>
                </TableContainer>
                <TablePagination
                    className="mr-4"
                    rowsPerPageOptions={[10, 25, 100]}
                    component="div"
                    count={plist.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onChangePage={handleChangePage}
                    onChangeRowsPerPage={handleChangeRowsPerPage}
                />
        </div>
    )
}

export default Patient_list
